import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

function DashboardPage() {
  const username = localStorage.getItem('username');
  const token = localStorage.getItem('jwt');
  const months = [
    'January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December',
  ];

  function decodingJWT(token) {
    try {
      const base64payload = (token.split('.')[1]).replace(/-/g, '+').replace(/_/g, '/');
      const jsonPayload = decodeURIComponent(atob(base64payload).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
      }).join(''));

      return JSON.parse(jsonPayload);
    } catch (error) {
      return {};
    }
  }

  useEffect(() => {
    const existingToken = localStorage.getItem('jwt');
    if (existingToken) {
      const decodedToken = decodingJWT(existingToken);
      const issuedAt = decodedToken.iat; 
      const expiresIn = decodedToken.exp - issuedAt;
      const currentTimeInSeconds = Math.floor(Date.now() / 1000);
      const warningTime = expiresIn - 20;
      const warningTimeout = setTimeout(() => {
        const userResponse = window.confirm('Your session will expire in 20 seconds. Do you want to continue?');

        if (!userResponse || currentTimeInSeconds-issuedAt>expiresIn) {
          localStorage.removeItem('jwt');
          localStorage.removeItem('username');
          window.location.reload();
        }
      }, warningTime * 1000);

      return () => clearTimeout(warningTimeout);
    }
  }, []);

  const [selectedMonth, setSelectedMonth] = useState('');
  const [selectedYear, setSelectedYear] = useState('');
  const [dataSource, setDataSource] = useState({
    datasets: [
      {
        data: [],
        backgroundColor: [],
        expense: [],
        month: [],
      },
    ],
    labels: [],
  });

  const [totalBudget, setTotalBudget] = useState(0);
  const [totalExpense, setTotalExpense] = useState(0);

  const createDataTable = () => {
    // Existing code for creating data table
  };

  const getBudget = (selectedMonth) => {
    axios
      .get(`http://localhost:3000/api/budget?q=${selectedMonth}`, { //${username}
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then(function (res) {
        // Existing code for fetching and processing budget data
      })
      .catch(function (error) {
        console.error('Error fetching budget data:', error);
      });
  };

  useEffect(() => {
    getBudget(selectedMonth);
  }, [selectedMonth]);

  const getFormattedDate = () => {
    // Existing code for formatting date
  };

  const handleMonthChange = (event) => {
    setSelectedMonth(event.target.value);
  };

  const handleYearChange = (event) => {
    setSelectedYear(event.target.value);
  };

  const currentMonth = new Date().getMonth(); // Months are zero-indexed
  const currentYear = new Date().getFullYear();

  useEffect(() => {
    setSelectedMonth((currentMonth + 1).toString());
    setSelectedYear(currentYear.toString());
  }, []);

  const monthNames = [
    'Jan', 'Feb', 'Mar', 'Apr', 'May', 'Jun',
    'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'
  ];

  const allMonths = monthNames;

  const years = [currentYear, currentYear - 1].map((year) => year);

  return (
    <div style={{ position: 'relative', padding: '30px' }}>
      <div className="dashboard-corner-section">
        <div className="heading-container">
          <h1 className="dashboard-head">WELCOME TO {username}'s DASHBOARD</h1>
          <Link to="/login" className="logout-link">Logout</Link>
          <label htmlFor="month-dropdown">Select a month:</label>
          <select id="month-dropdown" value={selectedMonth} onChange={handleMonthChange}>
            <option value="" disabled>Select a month</option>
            {months.map((month) => (
              <option key={month} value={month}>{month}</option>
            ))}
          </select>
        </div>
        <div className="dropdown-container">
          <select
            id="months"
            value={selectedMonth}
            onChange={handleMonthChange}
          >
            {allMonths.map((month, index) => (
              <option key={month} value={(index + 1).toString()}>
                {month}
              </option>
            ))}
          </select>

          <select
            id="years"
            value={selectedYear}
            onChange={handleYearChange}
          >
            {years.map((year) => (
              <option key={year} value={year}>
                {year}
              </option>
            ))}
          </select>
        </div>
        <section className="charts">
          {/* Charts section removed */}
        </section>
        <div id="table-container" className="data-table-container"></div>
      </div>
    </div>
  );
}

export default DashboardPage;
