import { Link, useNavigate } from 'react-router-dom';
import axios from 'axios';
import React, { useState, useEffect } from 'react';

function Signup() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [username, setUsername] = useState('');
  const [name, setName] = useState('');
  const [isPopupOpen, setPopupOpen] = useState(false);
  const [popupTimer, setPopupTimer] = useState(null);
  const navigate = useNavigate();

  function handleSubmit(event) {
    event.preventDefault();

    axios.post('http://localhost:3000/api/signup', { email, password, username, name })
      .then(res => {
        if (res && res.data && res.data.success) {
          setPopupOpen(true);

          // Close the popup after a certain duration (e.g., 3 seconds)
          const timer = setTimeout(() => {
            setPopupOpen(false);

            // Navigate to login page after closing the popup
            navigate('/login');
          }, 3000);

          // Save the timer in state to clear it if needed
          setPopupTimer(timer);
        }
      })
      .catch(err => console.error(err));
  }

  // Clear the popup timer when the component is unmounted
  useEffect(() => {
    return () => {
      if (popupTimer) {
        clearTimeout(popupTimer);
      }
    };
  }, [popupTimer]);

  function handleBackToRoot() {
    navigate('/'); // Update the route to your dashboard route
  }

  return (
    <div className="login-page">
      <link href='https://unpkg.com/boxicons@2.1.4/css/boxicons.min.css' rel='stylesheet'></link>
      <button className="back" onClick={handleBackToRoot}>
        <i className='bx bxs-left-arrow-alt'></i>
        Back to HomePage
      </button>
      <div className="login-box">
        <form id="login-form" onSubmit={handleSubmit}>
          <p className="form-title">New Here?</p>
          <p>Signup to the Dashboard</p>
          <div className="input-box">
            <label htmlFor="name">Name:</label>
            <input type="text" id="name" placeholder='Name' value={name} onChange={e => setName(e.target.value)} required />
            <i className='bx bxs-user' aria-hidden="true" aria-label="Name icon"></i>
          </div>
          <div className="input-box">
            <label htmlFor="signup-email">Email:</label>
            <input type="text" id="signup-email" placeholder='Email' value={email} onChange={e => setEmail(e.target.value)} required />
            <i className='bx bxs-envelope' aria-hidden="true" aria-label="Email icon"></i>
          </div>
          <div className="input-box">
            <label htmlFor="username">Username:</label>
            <input type="text" id="username" placeholder='Username' value={username} onChange={e => setUsername(e.target.value)} required />
            <i className='bx bxs-user' aria-hidden="true" aria-label="Username icon"></i>
          </div>
          <div className="input-box">
            <label htmlFor="signup-password">Password:</label>
            <input type="password" id="signup-password" placeholder='Password' value={password} onChange={e => setPassword(e.target.value)} required />
            <i className='bx bxs-lock-alt' aria-hidden="true" aria-label="Password icon"></i>
          </div>
          <button id="signup" type="submit" className="login-form-button">Sign Up</button>
          <br/><br/>
          <div className="createacc">
            Already have an account?&nbsp;
            <Link itemProp="url" to="/login">Login Here</Link>
          </div>
        </form>
      </div>
      {isPopupOpen && (
        <div className="popup">
          <div className="popup-content">
            <p>Account has been created successfully!</p>
          </div>
        </div>
      )}
    </div>
  );
};

export default Signup;
