  const ctx = document.getElementById('chart3').getContext('2d');
  let existingChart = Chart.getChart(ctx);
  if (existingChart) {
    existingChart.destroy();
  }

  // Filter data based on the selected month
  const filteredData = dataSource.datasets[0].data.filter(
    (_, index) => dataSource.datasets[0].month[index] === selectedMonth3
  );

  // Calculate total budget and total expense
  let totalBudget = 0;
  let totalExpense = 0;
  filteredData.forEach((item) => {
    totalBudget += item;
  });
  dataSource.datasets[0].expense.forEach((expense, index) => {
    if (dataSource.datasets[0].month[index] === selectedMonth3) {
      totalExpense += expense;
    }
  });

  existingChart = new Chart(ctx, {
    type: 'bar',
    data: {
      labels: ['Total Budget', 'Total Expense'],
      datasets: [
        {
          label: 'Amount',
          data: [totalBudget, totalExpense],
          backgroundColor: ['#36a2eb', '#ff6384'],
          borderWidth: 1
        }
      ]
    },
    options: {
      scales: {
        y: {
          beginAtZero: true
        }
      }
    }
  });
};


  const getBudget = () => {
    axios
      .get(`http://localhost:3000/api/budget/q`, { //${username}
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then(function (res) {
        console.log(res.data);
        let budgetTotal = 0;
        let expenseTotal = 0;
        for (var i = 0; i < res.data.length; i++) {
          budgetTotal += res.data[i].budget;
          expenseTotal += res.data[i].expense;
          dataSource.datasets[0].data[i] = res.data[i].budget;
          dataSource.labels[i] = res.data[i].title;
          dataSource.datasets[0].backgroundColor[i] = res.data[i].color;
          dataSource.datasets[0].expense[i] = res.data[i].expense;
          dataSource.datasets[0].month[i] = res.data[i].month;
        }
        setTotalBudget(budgetTotal);
        setTotalExpense(expenseTotal);
        console.log('dataSource:::',dataSource);
        drawDonutChart();
        createDataTable();
        drawTotalChart(); // Draw the total chart after getting the data
      })
      .catch(function (error) {
        console.error('Error fetching budget data:', error);
      });
  };

  useEffect(() => {
    getBudget();
  }, []);

  useEffect(() => {
    drawDonutChart(); // Redraw chart when selectedMonth1 changes
  }, [selectedMonth1]);

  useEffect(() => {
    drawDonutChart(); // Redraw chart when dataSource changes
  }, [dataSource]);

  useEffect(() => {
    drawBarChart2(); // Fetch initial data for chart2 and draw the chart
  }, [selectedMonth2, dataSource]); // Add dataSource as a dependency

  useEffect(() => {
    drawBarChart2(); // Refetch data and redraw chart2 when selectedMonth2 changes
  }, [selectedMonth2]);

  const handleMonthChange = (event) => {
    const newSelectedMonth = event.target.value;
    setSelectedMonth(newSelectedMonth);
    setSelectedMonth1(newSelectedMonth); // Update selectedMonth1 with the new value
    setSelectedMonth2(newSelectedMonth); // Update selectedMonth2 with the new value
    setSelectedMonth3(newSelectedMonth); // Update selectedMonth3 with the new value
  };

  const handleChange1 = (event) => {
    setSelectedMonth1(event.target.value);
    drawDonutChart(); // Redraw chart when selectedMonth1 changes
  };

  const handleChange2 = (event) => {
    setSelectedMonth2(event.target.value);
    // Redraw chart2 only when the selected month changes, not on initial render
  };

  const handleChange3 = (event) => {
    setSelectedMonth3(event.target.value);
    // Redraw chart3 only when the selected month changes, not on initial render
  };

  return (
    <div style={{ position: 'relative', padding: '30px' }}>
      <div className="dashboard-corner-section">
        <div className="heading-container">
          <h1 className="dashboard-head">WELCOME TO {username}'s DASHBOARD</h1>
          <Link to="/login" className="logout-link">Logout</Link>
        </div>
        <div className='filter-dropdown'>
          <label htmlFor="month-dropdown">Select a month:</label><br/>
          <div className="custom-dropdown">
            <select id="month-dropdown" value={selectedMonth} onChange={handleMonthChange}>
              {months.map((month, index) => (
                <option key={index} value={month}>{month}</option>
              ))}
            </select>
            <div className="dropdown-arrow">&#9660;</div>
          </div>
        </div>
        <section className="charts">
          <article className="chart chart1">
            <h3>Donut Chart for Expenses in {selectedMonth1}</h3>
            <label>
              Select a month:
              <select value={selectedMonth1} onChange={handleChange1} aria-label="Select a month">
                {months.map((month, index) => (
                  <option key={index} value={month}>{month}</option>
                ))}
              </select>
            </label>
            <div>
              <canvas id="chart1"></canvas>
            </div>
          </article>
          <article className="chart chart2">
            <h3>Bar Chart for Budget and Expenses in {selectedMonth2}</h3>
            <label>
              Select a month:
              <select value={selectedMonth2} onChange={handleChange2} aria-label="Select a month">
                {months.map((month, index) => (
                  <option key={index} value={month}>{month}</option>
                ))}
              </select>
            </label>
            <div>
              <canvas id="chart2"></canvas>
            </div>
          </article>

          <article className="chart chart3"> {/* Corrected the class name from chart2 to chart3 */}
            <h3>Total Budget and Total Expenses in {selectedMonth3}</h3> {/* Updated the month variable */}
            <label>
              Select a month:
              <select value={selectedMonth3} onChange={handleChange3} aria-label="Select a month"> {/* Corrected the value and onChange function */}
                {months.map((month, index) => (
                  <option key={index} value={month}>{month}</option>
                ))}
              </select>
            </label>
            <div>
              <canvas id="chart3"></canvas>
            </div>
          </article>


        </section>
        <div id="table-container" className="data-table-container"></div>
      </div>
    </div>
  );
}

export default DashboardPage;
