import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { Link } from 'react-router-dom';

function DeleteCategory() {
  const [title, setTitle] = useState('');
  const [month, setMonth] = useState('');
  const [existingTitles, setExistingTitles] = useState([]);
  const [titlesMap, setTitlesMap] = useState([]);
  const [isPopupOpen, setPopupOpen] = useState(false);
  const navigate = useNavigate();
  const username = localStorage.getItem('username');
  const [token, setToken] = useState(localStorage.getItem('jwt') || '');

  function decodingJWT(token) {
    try {
      const base64payload = (token.split('.')[1]).replace(/-/g, '+').replace(/_/g, '/');
      const jsonPayload = decodeURIComponent(atob(base64payload).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
      }).join(''));

      return JSON.parse(jsonPayload);
    } catch (error) {
      console.error('Error decoding JWT token:', error);
      return {};
    }
  }

  useEffect(() => {
    if (token) {
      const decodedToken = decodingJWT(token);
      const issuedAt = decodedToken.iat;
      const expiresIn = decodedToken.exp - issuedAt;
      const currentTimeInSeconds = Math.floor(Date.now() / 1000);
      const warningTime = expiresIn - 20;
      const warningTimeout = setTimeout(() => {
        const userResponse = window.confirm('Your session will expire in 20 seconds. Do you want to continue?');

        if (!userResponse || currentTimeInSeconds - issuedAt > expiresIn) {
          // User clicked 'Cancel', logout the user
          localStorage.removeItem('jwt');
          localStorage.removeItem('username');
          setToken('');
          window.location.reload();
        }
      }, warningTime * 1000);

      return () => clearTimeout(warningTimeout);
    }
  }, [token]);

  const handleLogout = () => {
    localStorage.removeItem('jwt');
    localStorage.removeItem('username');
    setToken('');
    navigate('/');
  };

  const handleBackToDashboard = () => {
    navigate('/dashboard');
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    axios
      .post(
        'http://localhost:3000/deletebudget',
        {
          title,
          username,
          month,
        },
        {
          headers: {
            Authorization: `Bearer ${token}`,
          },
        }
      )
      .then((res) => {
        if (res.data.success) {
          setPopupOpen(true);
          setTimeout(() => {
            setPopupOpen(false);
          }, 3000);
        }
      })
      .catch((err) => console.error(err));
  };

  const getTitles = () => {
    axios
      .get(`http://localhost:3000/gettitles/${username}`, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then((res) => {
        const titlesMap = {};

        res.data.forEach(({ title, month }) => {
          if (!titlesMap[title]) {
            titlesMap[title] = [];
          }

          titlesMap[title].push(month);
        });

        setTitlesMap(titlesMap);
        setExistingTitles(Object.keys(titlesMap));
      })
      .catch((error) => {
        console.error('Error fetching titles data:', error);
      });
  };

  useEffect(() => {
    getTitles();
  }, [token]);

  const getMonthsForTitle = (selectedTitle) => {
    return titlesMap[selectedTitle] || [];
  };

  return (
    <div className="login-page">
      <link href="https://unpkg.com/boxicons@2.1.4/css/boxicons.min.css" rel="stylesheet" />
      <div className="logout-btn">
        <button className="logout" onClick={handleLogout}>
          Logout
        </button>
      </div>
      <button className="back" onClick={handleBackToDashboard}>
        <i className="bx bxs-left-arrow-alt"></i>
        Back to Dashboard
      </button>
      <div className="login-box">
        <form id="login-form" onSubmit={handleSubmit}>
          <p className="form-title">Delete Category</p>
          <div className="input-box">
            <label htmlFor="title">Title:</label>
            <div className="custom-dropdown">
              <select
                id="title"
                onChange={(e) => {
                  setTitle(e.target.value);
                  setMonth('');
                }}
                required
              >
                <option value="" disabled selected>
                  Select Title
                </option>
                {existingTitles.map((existingTitle, index) => (
                  <option key={`${existingTitle}-${index}`} value={existingTitle}>
                    {existingTitle}
                  </option>
                ))}
              </select>
              <div className="dropdown-arrow">&#9660;</div>
            </div>
          </div>
          <br/>
          <div className="input-box">
            <label htmlFor="month">Month:</label>
            <div className="custom-dropdown">
              <select id="month" onChange={(e) => setMonth(e.target.value)} required disabled={!title}>
                <option value="" disabled selected>
                  Select Month
                </option>
                {getMonthsForTitle(title).map((month, index) => (
                  <option key={`${title}-${month}-${index}`} value={month}>
                    {month}
                  </option>
                ))}
              </select>
              <div className="dropdown-arrow">&#9660;</div>
            </div>
          </div>
          <br/>
          <br/>
          <button id="delete" type="submit" className="login-form-button">
            Delete
          </button>
        </form>
      </div>

      {isPopupOpen && (
        <div className="popup">
          <div className="popup-content">
            <p>Data has been deleted successfully!</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default DeleteCategory;
