import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';
import Chart from 'chart.js/auto';

function DashboardPage() {
  const username = localStorage.getItem('username');
  const token = localStorage.getItem('jwt');
  const months = [
    'January', 'February', 'March', 'April', 'May', 'June', 'July', 'August', 'September', 'October', 'November', 'December',
  ];

  function decodingJWT(token) {
    try {
      const base64payload = (token.split('.')[1]).replace(/-/g, '+').replace(/_/g, '/');
      const jsonPayload = decodeURIComponent(atob(base64payload).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
      }).join(''));

      return JSON.parse(jsonPayload);
    } catch (error) {
      return {};
    }
  }

  useEffect(() => {
    const existingToken = localStorage.getItem('jwt');
    if (existingToken) {
      const decodedToken = decodingJWT(existingToken);
      const issuedAt = decodedToken.iat; 
      const expiresIn = decodedToken.exp - issuedAt;
      const currentTimeInSeconds = Math.floor(Date.now() / 1000);
      const warningTime = expiresIn - 20;
      const warningTimeout = setTimeout(() => {
        const userResponse = window.confirm('Your session will expire in 20 seconds. Do you want to continue?');

        if (!userResponse || currentTimeInSeconds-issuedAt>expiresIn) {
          localStorage.removeItem('jwt');
          localStorage.removeItem('username');
          window.location.reload();
        }
      }, warningTime * 1000);

      return () => clearTimeout(warningTimeout);
    }
  }, []);

  const [selectedMonth, setSelectedMonth] = useState('January');
  const [selectedMonth1, setSelectedMonth1] = useState('January');
  const [selectedMonth2, setSelectedMonth2] = useState('January');
  const [dataSource, setDataSource] = useState({
    datasets: [
      {
        data: [],
        backgroundColor: [],
        expense: [],
        month: [],
      },
    ],
    labels: [],
  });
  const [dataSource2, setDataSource2] = useState({
    datasets: [
      {
        data: [],
        backgroundColor: [],
        month: [],
      },
    ],
    labels: [],
  });

  const [totalBudget, setTotalBudget] = useState(0);
  const [totalExpense, setTotalExpense] = useState(0);

  const createDataTable = () => {
    // Existing code for creating data table
  };

  const drawDonutChart = () => {
    const ctx = document.getElementById('chart1').getContext('2d');
    let existingChart = Chart.getChart(ctx);
    if (existingChart) {
      existingChart.destroy();
    }

    const filteredExpenses = dataSource.datasets[0].expense.filter(
      (expense, index) => dataSource.datasets[0].month[index] === selectedMonth1
    );
    const filteredLabels = dataSource.labels.filter(
      (_, index) => dataSource.datasets[0].month[index] === selectedMonth1
    );
    const filteredColors = dataSource.datasets[0].backgroundColor.filter(
      (_, index) => dataSource.datasets[0].month[index] === selectedMonth1
    );

    if (filteredLabels.length === 0) {
      ctx.clearRect(0, 0, ctx.canvas.width, ctx.canvas.height);
      ctx.font = '20px Arial';
      ctx.textAlign = 'center';
      ctx.fillText('No data available for this month', ctx.canvas.width / 2, ctx.canvas.height / 2);
    } else {
      existingChart = new Chart(ctx, {
        type: 'doughnut',
        data: {
          labels: filteredLabels,
          datasets: [
            {
              label: 'Expenses',
              data: filteredExpenses,
              backgroundColor:filteredColors,
              borderWidth: 1,
            },
          ],
        },
      });
    }
  };

  const drawBarChart2 = () => {
    const ctx = document.getElementById('chart2').getContext('2d');
    let existingChart = Chart.getChart(ctx);
    if (existingChart) {
      existingChart.destroy();
    }

    // Logic to draw positive-negative bar chart based on dataSource2
    // Example:
    existingChart = new Chart(ctx, {
      type: 'bar',
      data: {
        labels: dataSource2.labels,
        datasets: [
          {
            label: 'Budget',
            data: dataSource2.datasets[0].data,
            backgroundColor: 'rgba(54, 162, 235, 0.5)',
            borderColor: 'rgba(54, 162, 235, 1)',
            borderWidth: 1
          },
          {
            label: 'Expenses',
            data: dataSource2.datasets[0].expense,
            backgroundColor: 'rgba(255, 99, 132, 0.5)',
            borderColor: 'rgba(255, 99, 132, 1)',
            borderWidth: 1
          }
        ]
      },
      options: {
        scales: {
          y: {
            beginAtZero: true
          }
        }
      }
    });
  };

  const getBudget = () => {
    axios
      .get(`http://localhost:3000/api/budget/q`, { //${username}
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then(function (res) {
        console.log(res.data);
        let budgetTotal = 0;
        let expenseTotal = 0;
        for (var i = 0; i < res.data.length; i++) {
          budgetTotal += res.data[i].budget;
          expenseTotal += res.data[i].expense;
          dataSource.datasets[0].data[i] = res.data[i].budget;
          dataSource.labels[i] = res.data[i].title;
          dataSource.datasets[0].backgroundColor[i] = res.data[i].color;
          dataSource.datasets[0].expense[i] = res.data[i].expense;
          dataSource.datasets[0].month[i] = res.data[i].month;
        }
        setTotalBudget(budgetTotal);
        setTotalExpense(expenseTotal);
        console.log('dataSource:::',dataSource);
        drawDonutChart();
        createDataTable();
      })
      .catch(function (error) {
        console.error('Error fetching budget data:', error);
      });
  };

  const fetchDataAndDrawChart2 = () => {
    axios
      .get(`http://localhost:3000/api/budget/q`, {
        params: {
          month: selectedMonth2,
        },
        headers: {
          Authorization: `Bearer ${token}`,
        },
      })
      .then(function (res) {
        const newData = res.data.map(item => ({
          title: item.title,
          budget: item.budget,
          expense: item.expense,
          color: item.color,
          month: item.month,
        }));
        setDataSource2(newData);
        drawBarChart2();
      })
      .catch(function (error) {
        console.error('Error fetching data for chart2:', error);
      });
  };

  useEffect(() => {
    getBudget();
  }, []);

  useEffect(() => {
    drawDonutChart(); // Redraw chart when selectedMonth1 changes
  }, [selectedMonth1]);

  useEffect(() => {
    drawDonutChart(); // Redraw chart when dataSource changes
  }, [dataSource]);

  useEffect(() => {
    fetchDataAndDrawChart2(); // Fetch initial data for chart2 and draw the chart
  }, []);

  useEffect(() => {
    fetchDataAndDrawChart2(); // Refetch data and redraw chart2 when selectedMonth2 changes
  }, [selectedMonth2]);

  const handleMonthChange = (event) => {
    const newSelectedMonth = event.target.value;
    setSelectedMonth(newSelectedMonth);
    setSelectedMonth1(newSelectedMonth); // Update selectedMonth1 with the new value
    setSelectedMonth2(newSelectedMonth); // Update selectedMonth2 with the new value
  };

  const handleChange1 = (event) => {
    setSelectedMonth1(event.target.value);
    drawDonutChart(); // Redraw chart when selectedMonth1 changes
  };

  const handleChange2 = (event) => {
    setSelectedMonth2(event.target.value);
    fetchDataAndDrawChart2(); // Fetch data and redraw chart2 when selectedMonth2 changes
  };

  return (
    <div style={{ position: 'relative', padding: '30px' }}>
      <div className="dashboard-corner-section">
        <div className="heading-container">
          <h1 className="dashboard-head">WELCOME TO {username}'s DASHBOARD</h1>
          <Link to="/login" className="logout-link">Logout</Link>
        </div>
        <div className='filter-dropdown'>
          <label htmlFor="month-dropdown">Select a month:</label><br/>
          <div className="custom-dropdown">
            <select id="month-dropdown" value={selectedMonth} onChange={handleMonthChange}>
              <option value="" disabled>Select a month</option>
              {months.map((month, index) => (
                <option key={index} value={month}>{month}</option>
              ))}
            </select>
            <div className="dropdown-arrow">&#9660;</div>
          </div>
        </div>
        <section className="charts">
          <article className="chart chart1">
            <h3>Donut Chart for Expenses in {selectedMonth1}</h3>
            <label>
              Select a month:
              <select value={selectedMonth1} onChange={handleChange1} aria-label="Select a month">
                {months.map((month, index) => (
                  <option key={index} value={month}>{month}</option>
                ))}
              </select>
            </label>
            <div>
              <canvas id="chart1"></canvas>
            </div>
          </article>
          <article className="chart chart2">
            <h3>Positive-Negative Bar Chart for Budget and Expenses</h3>
            <label>
              Select a month:
              <select value={selectedMonth2} onChange={handleChange2} aria-label="Select a month">
                {months.map((month, index) => (
                  <option key={index} value={month}>{month}</option>
                ))}
              </select>
            </label>
            <div>
              <canvas id="chart2"></canvas>
            </div>
          </article>
        </section>
        <div id="table-container" className="data-table-container"></div>
      </div>
    </div>
  );
}

export default DashboardPage;
